DROP SCHEMA IF EXISTS `Millionaires`;
CREATE SCHEMA `Millionaires` DEFAULT CHARACTER SET utf8 COLLATE utf8_polish_ci ;
USE `Millionaires`;
DROP TABLE IF EXISTS `Questions`;
CREATE TABLE `Questions` (
	`id` int(3) NOT NULL AUTO_INCREMENT,
	`question` varchar(200) NOT NULL ,
	`answerA` varchar(100) NOT NULL ,
	`answerB` varchar(100) NOT NULL ,
	`answerC` varchar(100) NOT NULL ,
	`answerD` varchar(100) NOT NULL ,
	`correctAnswer` varchar(1) NOT NULL ,
	`points` int(7) NOT NULL ,
	PRIMARY KEY (`id`)
);
ALTER DATABASE Millionaires CHARACTER SET utf8 COLLATE utf8_polish_ci;



















